import React, { useContext, useState, useEffect } from 'react'
import { Redirect } from 'react-router-dom'
import firebaseConfig from '../config'


const CheckRandom = () => {

    const db = firebaseConfig.firestore();
    const [count, setCount] = useState([]);
    let countP1team = 0;
    let countP2team = 0;
    function getCurrentUserOnWeb() {
        db.collection('user').onSnapshot(snapshot => {
            setCount(snapshot.docs.map(doc => doc.data()))
        })

    }
    count.map(data => {
        //console.log(data.isP1team)
        if (data.isOnline) {
            //console.log(data.isP1team)
            if (data.isP1team) {
                countP1team++;
                alert("p1 "+countP1team)
            }
            else if (data.isP2team) {
                countP2team++;
            }
        }
    })

    function ConditionToMain() {
        if (countP1team - countP2team > 1) {
            window.location.href = "/MainP2"
        }
        else if (countP2team - countP1team > 2) {
            window.location.href = "/MainP1"
        }
        else {
            function getRandomInt(min, max) {
                min = Math.ceil(min);
                max = Math.floor(max);
                return Math.floor(Math.random() * (max - min) + min); //The maximum is exclusive and the minimum is inclusive
            }
            let random = getRandomInt(0, 2)

            //alert(random)
            if (random === 0) {
                alert("asdasda 0")
                

                    window.location.href = "/MainP1"
                
            }
            else {
                alert("asdasda 1")
               

                window.location.href = "/MainP2"
                
            }
        }

        



    }

    ConditionToMain();

    useEffect(() => {
        getCurrentUserOnWeb();
    }, [])

    return (
        <p>Loading...</p>
    )
}

export default CheckRandom;
