import React, { useContext, useState, useEffect } from 'react'
import { Redirect } from 'react-router-dom'
import { AuthContext } from './auth'
import firebaseConfig from '../config'
import Nav from './Nav'
import Twitch from './Twitch'
import BtnP1 from './BtnP1'
import { Alert, Confirm } from 'react-st-modal'


const MainP1 = () => {

    //const db = firebaseConfig.firestore();
    //let countUser=0;
    const { currentUser } = useContext(AuthContext);
    //let checkUser = false;
    const db = firebaseConfig.firestore();
    const [checkUser, setcheckUser] = useState(true);
    const [isRunning, setIsRunning] = useState(true);
    const [seconds, setSeconds] = useState(0);

    let checkTotalUser;
    const [count, setCount] = useState([])
    function getCurrentUserOnWeb() {
        //console.log("czxczxczxc")
        db.collection('countuser').doc('onweb').onSnapshot(doc => {
            const item = [];
            item.push(doc.data())
            setCount(item);
            checkTotalUser = doc.data().p1team
            //alert(count)
            //alert(doc.data().p1team)
        })

    }







    useEffect(() => {
        //console.log(count)
        //console.log("asdasdasdasd")

        getCurrentUserOnWeb();
        Alert("Welcome to Player 1 team!!!!!", 'Welcome')


    }, [])

    useEffect(() => {
        if (isRunning) {
            //alert("re")
            const id = window.setInterval(() => {
                db.collection('user').doc(currentUser.uid).get().then(data => {
                    const checkanswer1 = data.data().answerFirstQuestion
                    const checkanswer2 = data.data().answerSecordQuestion
                    setSeconds(seconds => seconds + 1)
                    if (checkanswer1 && checkanswer2) {
                        setIsRunning(isRunning => false)
                        console.log(checkanswer1, checkanswer2)
                        //console.log("isRunning:"+isRunning)
                    }
                    else {



                        window.location.href = "/submit2"
                        // Ð¡onfirmation confirmed


                        //Alert("Please do the second questionnaire on this link:" + " https://www.surveymonkey.com/r/6KHX3TK.", 'Please do a questionnaire');
                        //window.location.href = "/submit2"
                    }

                    //console.log("isRunning:"+isRunning)
                })

            }, 10000)
            //console.log("isRunning:"+isRunning)
            return () => window.clearInterval(id);
        }

    }, [isRunning])


    if (!currentUser) {

        //setcheckUser(true);
        return <Redirect to="/login" />;
    }

    if (currentUser && checkUser) {
        db.collection('user').doc(currentUser.uid).update({
            isP2team: false,
            isP1team: true

        })
        //setcheckLogout(false)
        setcheckUser(false);
        //alert("logout")

    }

    return (
        <div>
            <Nav />
            <Twitch />
            <BtnP1 />
            {seconds}
            <button className="btn btn-success" onClick={() => setIsRunning(false)}>P1+</button>

            <div className="container">
                <div className="row">
                    <div className="col-lg-8 mx-auto text-center mb-0 mt-3 ">
                        <h1>Leaderboard</h1>
                    </div>

                </div>

                <div className="row">
                    <div className="col-lg-12 mx-auto text-center mb-0 ">
                        <div className="responsive-iframe">




                            <iframe src="https://docs.google.com/spreadsheets/d/e/2PACX-1vSkQOavXwJjbSuUDdtspqjEwmbgCLc2pC7EromgysGZrmX0BJmm1x_6mRRqj71RJnMcj_n4CBld9McT/pubhtml?gid=349047516&amp;single=true&amp;widget=true&amp;headers=false" width="75%" height="300px" ></iframe>
                        </div>

                    </div>
                </div>
            </div>


        </div>

    )

}

export default MainP1;
