import React, { useContext } from 'react'
import { Redirect } from 'react-router-dom'
import { AuthContext } from './auth'
import firebaseConfig from '../config'

const MainP1 = () => {
    const { currentUser } = useContext(AuthContext);

    if (!currentUser) {
        return <Redirect to="/login" />;
    }

    return (


        <div className="container ">
            <div className="row">
                <div className="col-lg-12 mx-auto text-center mb-0 ">
                    <label className="col-md-2">Display Name:</label>
                    <span className="col-md-4" id="display-name">{currentUser.displayName}</span>
                    <p><Link onClick={() => firebaseConfig.auth().signOut()} >Sign out</Link></p>
                
                </div>
            </div>
            <div>
            <div className="container mt-5">
                <h1>Welcome</h1>
                <p>this sdasdasdasdasdasd: {currentUser.displayName}</p>
                <button onClick={() => firebaseConfig.auth().signOut()} className="btn btn-ganger">Sign out</button>
            </div>
            
            

        </div>


        </div>
        


    )
}

export default MainP1;
