import React, { useContext, useState, useEffect } from 'react'
import { Redirect } from 'react-router-dom'
import { AuthContext } from './auth'
import firebaseConfig from '../config'
import Nav from './Nav'
import Twitch from './Twitch'
import BtnP1 from './BtnP1'


const MainP1 = () => {

    //const db = firebaseConfig.firestore();
    //let countUser=0;
   
    const { currentUser } = useContext(AuthContext);
    //let checkUser = false;
    const db = firebaseConfig.firestore();
    const [countUser, setCountUser] = useState(() => {
        return 0;
    })
    const [checkUser, setcheckUser] = useState(true);
    const [count, setCount] = useState([])
    function getCurrentUserOnWeb() {
        db.collection('countuser').doc('onweb').onSnapshot(doc => {
            const item = [];
            item.push(doc.data())
            setCount(item);
            //alert(doc.data().p1team)
        })

    }



    useEffect(() => {
        getCurrentUserOnWeb();
        window.onbeforeunload = () => {

            if (countUser < 0) {
                setCountUser(countUser = 0);
            }
            else {
                setCountUser(countUser - 1);
            }

            db.collection('countuser').doc('onweb').update({

                p1team: countUser

            })

        }

    }, [])

    if (!currentUser && !checkUser) {
        //alert(countUser)
        setCountUser(countUser - 1);
        setcheckUser(true);
        db.collection('countuser').doc('onweb').update({

            p1team: countUser

        })
        




        return <Redirect to="/login" />;
    }
    if (currentUser && checkUser) {

        db.collection('countuser').doc('onweb').get().then(data => {
            setCountUser(data.data().p1team)
            //alert(countUser)
        }).then(() => {
            setCountUser(countUser + 1);

            setcheckUser(false);
            db.collection('countuser').doc('onweb').update({

                p1team: countUser

            })

        })






    }

    return (
        <div>
            <Nav />
            <Twitch />
            <BtnP1 />
            <div>
                {
                    count.map(blog => {
                        return (
                            <div className="row row-cols-2" key={blog.p1team}>
                                <p >P1:{blog.p1team}</p>
                                <p>P2:{blog.p2team}</p>
                            </div>
                        )
                    })
                }
            </div>




            <div className="container">
                <div className="row">
                    <div className="col-lg-8 mx-auto text-center mb-0 mt-3 ">
                        <h1>Leaderboard</h1>
                    </div>

                </div>

                <div className="row">
                    <div className="col-lg-12 mx-auto text-center mb-0 ">
                        <div className="responsive-iframe">
                            <iframe src="https://docs.google.com/spreadsheets/d/e/2PACX-1vSkQOavXwJjbSuUDdtspqjEwmbgCLc2pC7EromgysGZrmX0BJmm1x_6mRRqj71RJnMcj_n4CBld9McT/pubhtml?gid=349047516&amp;single=true&amp;widget=true&amp;headers=false" width="75%" height="300px" ></iframe>
                        </div>

                    </div>
                </div>
            </div>


        </div>

    )

}

export default MainP1;
