import React, { useContext } from 'react'
import { Redirect } from 'react-router-dom'
import { AuthContext } from './auth'
import firebaseConfig from '../config'
import Button from 'react-bootstrap/Button'
import 'bootstrap/dist/css/bootstrap.min.css'



const ButtonP1team = () => {
    const { currentUser } = useContext(AuthContext);
    const tmi = require('tmi.js');
    const client = new tmi.Client({
        options: { debug: true, messagesLogLevel: "info" },
        connection: {
            reconnect: true,
            secure: true
        },
        identity: {
            username: 'ligoligo12',
            password: 'oauth:2adeuj6p5ui8ujtxmqwkf55sk5janf'
        },
        channels: [ 'ligoligo12' ]
    });
    client.connect().catch(console.error);


    const handleP1 = () => {
        client.say(`#ligoligo12`, `@${currentUser.displayName}, p1+: Fighting`);
    }
    const handleP2 = () => {
        client.say(`#ligoligo12`, `@${currentUser.displayName}, p2-: Fighting`);
    }

    if (!currentUser) {
        return <Redirect to="/login" />;
    }
    return (


        <div className="containerIF2">
            <div className="row row-cols-2">
                <Button variant="success" onClick={handleP1}>P1+</Button>
                <Button variant="danger" onClick={handleP2}>P2-</Button>
            </div>
            
        </div>



    )
}

export default ButtonP1team;
