import React, { useContext, useState, useEffect } from 'react'
import { Redirect } from 'react-router-dom'
import { AuthContext } from './auth'
import firebaseConfig from '../config'
import { Alert } from 'react-st-modal'



const SummitPasscode = () => {
    const { currentUser } = useContext(AuthContext);
    const db = firebaseConfig.firestore();
    const [checkUser, setcheckUser] = useState(true);
    const [count, setCount] = useState([]);
    let countP1team = 0;
    let countP2team = 0;
    function ConditionToMain() {
        if (countP1team - countP2team > 2) {
            alert("countP1team:" + countP1team + ">" + countP2team)
            window.location.href = "/MainP2"
        }
        else if (countP2team - countP1team > 2) {
            alert("countP2team:" + countP2team + ">" + countP1team)
            window.location.href = "/MainP1"
        }
        else {
            function getRandomInt(min, max) {
                min = Math.ceil(min);
                max = Math.floor(max);
                return Math.floor(Math.random() * (max - min) + min); //The maximum is exclusive and the minimum is inclusive
            }
            let random = getRandomInt(0, 2)

            //alert(random)
            if (random === 0) {
                alert("RANDOM 0 = countP1team: " + countP1team + "countP2team: " + countP2team)


                window.location.href = "/MainP1"

            }
            else {
                //alert("asdasda 1")
                alert("RANDOM 1 = countP1team: " + countP1team + "countP2team: " + countP2team)

                window.location.href = "/MainP2"

            }
        }
        db.collection('countuser').doc('onweb').update({

            p1team: countP1team,
            p2team: countP2team

        })



    }






    const handleSubmit = (e) => {
        e.preventDefault();
        const { passcode } = e.target.elements;
        db.collection('passcode').doc(currentUser.uid).get().then(data => {
            console.log(passcode.value)
            if (passcode.value === data.data().passcode1) {
                db.collection('user').doc(currentUser.uid).update({
                    passcode1: passcode.value,
                    submitData: Date(),
                    answerFirstQuestion: true

                }).then(() => {
                    db.collection('user').doc(currentUser.uid).update({
                        isOnline: true
                    }).then(() => {

                        db.collection('user').get().then(data => {
                            const items = [];
                            data.forEach(doc => {
                                items.push(doc.data())
                                //console.log("zxczxczxc "+doc.data())
                            });
                            console.log(items)
                            items.map(data => {
                                if (data.isOnline) {
                                    //console.log(data.isP1team)
                                    //console.log(data.isP2team)
                                    if (data.isP1team === true) {
                                        countP1team++;
                                        setcheckUser(countP1team)
                                        // console.log("p1 " + checkUser)
                                    }
                                    if (data.isP2team === true) {
                                        countP2team++;
                                        setcheckUser(countP2team)
                                        //console.log("p2 " + checkUser)
                                    }
                                }

                            })
                            ConditionToMain();
                            setCount(items);
                        })
                    })
                })
                //console.log(data.data().passcode1)
            } else {
                Alert("Wrong passcode!!!!!!", 'Error')
            }

        })
    }





    useEffect(() => {
        if (currentUser) {

            Alert("Please do a questionnaire on this link:" + " https://www.surveymonkey.com/r/6KHX3TK. After you done please copy passcode from questionnaire and press on this page", 'Please do a questionnaire');
            //alert(errorMessage);

        }


    }, [])

    if (!currentUser) {

        //console.log(currentUser.displayName)
        //alert(verified);
        //checkVerify(false);
        //getCurrentUserOnWeb();
        //getCurrentUserOnWeb();
        //alert(random)
        return (


            <Redirect to="/login" />

        )

    }





    return (

        <div className="container-fluid">
            <div className="row main-content text-center ">
                <div className="col-md-12 col-xs-12 col-sm-12 ">
                    <div className="container-fluid">
                        <div className="row">
                            <h2>Submit your passcode</h2>
                        </div>
                        <div className="row">
                            <form id="login-form" onSubmit={handleSubmit}>
                                <div className="row">
                                    <input type="text" name="passcode" id="passcode" className="form__input" placeholder="Press your passcode here"
                                        required />
                                </div>

                                <div className="row">
                                    <button className="btnlogin btn-success" type="submit">Submit</button>
                                </div>
                                <div className="row">
                                    <p>Please do a questionnaire: <a href="https://www.surveymonkey.com/r/6KHX3TK" target="_blank">Register Here</a></p>
                                </div>

                            </form>
                        </div>
                    </div>


                </div>


            </div>

        </div >



    )
}

export default SummitPasscode;
