import React, { useContext, useState, useEffect } from 'react'
import { Redirect } from 'react-router-dom'
import { AuthContext } from './auth'
import firebaseConfig from '../config'
import Nav from './Nav'
import Twitch from './Twitch'
import BtnP2 from './BtnP2'
import { Alert } from 'react-st-modal'


const MainP2 = () => {
    const { currentUser } = useContext(AuthContext);
    //let countUser=0;
    const [countUser, setCountUser] = useState(0)
    //let checkUser = false;
    const db = firebaseConfig.firestore();
    const [checkUser, setcheckUser] = useState(true);
    const [count, setCount] = useState([])
    function getCurrentUserOnWeb() {
        db.collection('countuser').doc('onweb').onSnapshot(doc => {
            const item = [];
            item.push(doc.data())
            setCount(item);
            //alert(doc.data().p2team)
        })

    }
    

    useEffect(() => {
        getCurrentUserOnWeb();
        Alert("Welcome to Player 2 team!!!!!", 'Welcome')
        
    }, [])

    if (!currentUser) {
        
        
        
        return <Redirect to="/login" />;
    }

    if (currentUser && checkUser) {
        db.collection('user').doc(currentUser.uid).update({
            isP2team: true,
            isP1team: false

        })
        //setcheckLogout(false)
        setcheckUser(false);
        //alert("logout")

        //return <Redirect to="/login" />;
    }

    return (
        <div>
            <Nav />
            <Twitch />
            <BtnP2 />
            {
                count.map(blog => {
                    return (
                        <div className="row">
                            <div className="col-lg-8 mx-auto text-center mb-0 mt-3 ">
                                <h3>{blog.p2team} users on Player 2 team</h3>
                            </div>

                        </div>

                    )
                })
            }




            <div className="container">
                <div className="row">
                    <div className="col-lg-8 mx-auto text-center mb-0 mt-3 ">
                        <h1>Leaderboard</h1>
                    </div>

                </div>

                <div className="row">
                    <div className="col-lg-12 mx-auto text-center mb-0 ">
                        <div className="responsive-iframe">
                            <iframe src="https://docs.google.com/spreadsheets/d/e/2PACX-1vSkQOavXwJjbSuUDdtspqjEwmbgCLc2pC7EromgysGZrmX0BJmm1x_6mRRqj71RJnMcj_n4CBld9McT/pubhtml?gid=349047516&amp;single=true&amp;widget=true&amp;headers=false" width="75%" height="300px" ></iframe>
                        </div>

                    </div>
                </div>
            </div>


        </div>

    )

}

export default MainP2;
