import React, { useContext, useState, useEffect } from 'react'
import { Redirect } from 'react-router-dom'
import firebaseConfig from '../config'


const  CheckRandom = () =>{
    const db = firebaseConfig.firestore();
    function getCurrentUserOnWeb() {
        db.collection('countuser').doc('onweb').onSnapshot(doc => {
            if(doc.data().p1team - doc.data().p2team > 2 )
            {
                return(
            
                    <Redirect to="/mainP2" />
                ) 
            }
            else if(doc.data().p2team - doc.data().p1team > 2){
                return(
            
                    <Redirect to="/mainP1" />
                ) 
            }
            else{
                const random = Math.random() * (0 - 1)
                if(random == 0){
                    return(
                        <Redirect to="/mainP1" />
                    ) 
                }
                else{
                    return(
            
                        <Redirect to="/mainP2" />
                    ) 
                }
            }
            
        })
        
    }
    
    /*useEffect(() => {
        getCurrentUserOnWeb();
    }, [])*/

    return(
        <p>{getCurrentUserOnWeb}</p>
    )
}

export default CheckRandom;
