import React, { useContext, useState, useEffect, Component } from 'react'
import { AuthContext } from './auth'
import firebaseConfig from '../config'
import { CustomDialog } from 'react-st-modal'



const Nav = () => {
    const { currentUser } = useContext(AuthContext);
    const db = firebaseConfig.firestore();
    const [blogs, setBlogs] = useState([])
    const fetchBlogs = async () => {
        const data = await db.collection('leaderboard').orderBy("countSendMessage", "desc").get();
        setBlogs(data.docs.map(doc => doc.data()))

    }
    useEffect(() => {
        fetchBlogs();
    }, [])

    function CustomDialogContent() {
        return (
            <div>
                <div class="container">
                    <div class="row">
                        <div class="col-lg-12 mx-auto text-center mb-0 ">
                            <div class="responsive-iframe">
                            <div className="row row-cols-2">
                            <p >Name</p>
                            <p >Score</p>
                            </div>
                                {
                                    blogs.map(blog => {
                                        return (
                                            <div className="row row-cols-2"> 
                                                <p key={blog.displayName}>{blog.displayName}</p>
                                                <p key={blog.displayName}>{blog.countSendMessage}</p>
                                            </div>
                                        )
                                    })
                                }
                            </div>
                        </div>
                    </div>
                </div>





            </div >
        );
    }


    return (
        <div className="thead-dark">
            <div className="row row-cols-2">
                <div className="col-lg-8 mx-auto text-center mb-0 mt-3 ">
                    <label className="col-md-2">Display Name:</label>
                    <span className="col-md-4" id="display-name">{currentUser.displayName}</span>
                    <label className="col-md-2"><button className="btn btn-outline-info" onClick={() => firebaseConfig.auth().signOut()} >Sign out</button></label>
                    <button className="btn btn-outline-info"
                        onClick={async () => {
                            const result = await CustomDialog(<CustomDialogContent />, {
                                title: 'Leaderboard',
                                showCloseIcon: true,
                            });
                        }}
                    >
                        Leaderboard
                    </button>
                </div>
            </div>
        </div>
    )

    


}

export default Nav;
