import React, { useContext } from 'react'
import { Redirect } from 'react-router-dom'
import { AuthContext } from './auth'
import firebaseConfig from '../config'
import { TwitchEmbed, TwitchChat, TwitchClip, TwitchPlayer } from 'react-twitch-embed';

const MainP2 = () => {
    const { currentUser } = useContext(AuthContext);
    const db = firebaseConfig.firestore(); 
    var countMessage;
    const tmi = require('tmi.js');
    const client = new tmi.Client({
        options: { debug: true, messagesLogLevel: "info" },
        connection: {
            reconnect: true,
            secure: true
        },
        identity: {
            username: 'ligoligo12',
            password: 'oauth:2adeuj6p5ui8ujtxmqwkf55sk5janf'
        },
        channels: ['ligoligo12']
    });
    client.connect().catch(console.error);
    
    
    const handleP1 = () => {
        db.collection('user').doc(currentUser.uid).get().then(data => {
            countMessage = data.data().countSendMessage
            //console.log(data.data().countSendMessage)
            //alert(countMessage)
       }).then(()=>{
        client.say(`#ligoligo12`, `@${currentUser.displayName}, p1-`);
        countMessage++
        db.collection('user').doc(currentUser.uid).update({

            countSendMessage: countMessage
            
        })
       })
        
        
    }
    const handleP2 = () => {
        client.say(`#ligoligo12`, `@${currentUser.displayName}, p2+`);
    }

    if (!currentUser) {
        return <Redirect to="/login" />;
    }

    return (
        <div>
            <div className="thead-dark">
                <div className="row row-cols-2">
                    <div className="col-lg-8 mx-auto text-center mb-0 mt-3 ">
                        <label className="col-md-2">Display Name:</label>
                        <span className="col-md-4" id="display-name">{currentUser.displayName}</span>
                        <label className="col-md-2"><button className="btn btn-outline-info" onClick={() => firebaseConfig.auth().signOut()} >Sign out</button></label>
                    </div>
                </div>
            </div>



            <div className="containerIF2">
                <div className="row row-cols-sm-1">
                    <div className="col-lg-12 mx-auto text-start mb-0 ">
                        <div className="containerIF">

                            <TwitchEmbed
                                channel="ligoligo12"
                                id="ligoligo12"
                                theme="dark"
                                width="100%"
                                height="100%"
                                onVideoPause={() => console.log(':(')}
                            />
                        </div>
                    </div>
                </div>
            </div>

            <div className="containerIF3">
                <div className="row row-cols-2">
                    <button className="btn btn-success" onClick={handleP2}>P2+</button>
                    <button className="btn btn-danger" onClick={handleP1}>P1-</button>
                </div>

            </div>

            <div class="container">
            <div class="row">
                <div className="col-lg-8 mx-auto text-center mb-0 mt-3 ">
                    <h1>Leaderboard</h1>
                    <h1>{countMessage}</h1>
                </div>

            </div>

            <div class="row">
                <div class="col-lg-12 mx-auto text-center mb-0 ">
                    <div class="responsive-iframe">
                        <iframe src="https://docs.google.com/spreadsheets/d/e/2PACX-1vSkQOavXwJjbSuUDdtspqjEwmbgCLc2pC7EromgysGZrmX0BJmm1x_6mRRqj71RJnMcj_n4CBld9McT/pubhtml?gid=349047516&amp;single=true&amp;widget=true&amp;headers=false" width="75%" height="300px" ></iframe>
                    </div>

                </div>
            </div>
        </div>


        </div>





    )
}

export default MainP2;
