import React, { useContext, useState } from 'react'
import { Redirect, Link } from 'react-router-dom'
import { AuthContext } from './auth'
import firebaseConfig from '../config'
import { CustomDialog, useDialog, Alert } from 'react-st-modal'


const LogIn = () => {
    const [verified, checkVerify] = useState(false);
    //checkVerify(false);
    const handleSubmit = (e) => {
        e.preventDefault();
        const { email, password } = e.target.elements;
        try {
            firebaseConfig.auth().signInWithEmailAndPassword(email.value, password.value).then(cred => {
                if (cred.user.emailVerified) {
                    checkVerify(true);
                } else {
                    CustomDialog(<CustomDialogContent />, {title: 'Custom Dialog',showCloseIcon: true,})
                    alert("Please verify your email");
                    checkVerify(false);
                }

                //console.log(cred.user)
            }).catch(error => {
                var errorMessage = error.message;
                Alert(errorMessage, 'Error!');
                //alert(errorMessage);
            })


        } catch (error) {
            Alert(error, 'Error!');
            //alert(error);
        }
    }

    const { currentUser } = useContext(AuthContext);
    //alert(verified);
    if (currentUser && verified) {
        //console.log(currentUser.displayName)
        //alert(verified);
        //checkVerify(false);
        return(
            
            <Redirect to="/mainP2" />
        ) 
    }

    return (


        <div className="container-fluid">
            <div className="row main-content bg-success text-center">
                <div className="col-md-4 text-center company__info">
                    <span className="company__logo">
                        <h2><span className="fa fa-android"></span></h2>
                    </span>

                </div>
                <div className="col-md-8 col-xs-12 col-sm-12 login_form ">
                    <div className="container-fluid">
                        <div className="row">
                            <h2>Log In</h2>
                        </div>
                        <div className="row">
                            <form id="login-form" onSubmit={handleSubmit}>
                                <div className="row">
                                    <input type="text" name="email" id="email" className="form__input" placeholder="Email"
                                        required />
                                </div>
                                <div className="row">

                                    <input type="password" name="password" id="password" className="form__input"
                                        placeholder="Password" required />
                                </div>

                                <div className="row">
                                    <button className="btnlogin btn-success" type="submit">Login</button>
                                </div>
                            </form>
                        </div>
                        <div className="row">
                            <p>Don't have an account? <Link to="/signup" >Register Here</Link></p>
                        </div>
                    </div>
                </div>
            </div>
        </div>

    )
}

function CustomDialogContent() {
    const dialog = useDialog();
  
    const [value, setValue] = useState();
  
    return (
      <div>
        <input
          type="text"
          onChange={(e) => {
            setValue(e.target.value);
          }}
        />
        <button
          onClick={() => {
            // Сlose the dialog and return the value
            dialog.close(value);
          }}
        >
          Custom button
        </button>
      </div>
    );
  }
  
  function CustomExample() {
    return (
     
        
        CustomDialog(<CustomDialogContent />, {title: 'Custom Dialog',showCloseIcon: true,})
        
   
    );
  }
  

export default LogIn;
