import React, { useContext, useState, useEffect } from 'react'
import { Redirect } from 'react-router-dom'
import firebaseConfig from '../config'


const  CheckRandom = () =>{
    const db = firebaseConfig.firestore();
    function getCurrentUserOnWeb() {
        db.collection('countuser').doc('onweb').onSnapshot(doc => {
            if(doc.data().p1team - doc.data().p2team > 2 )
            {
                return(
            
                    <Redirect to="/mainP2" />
                ) 
            }
            else if(doc.data().p2team - doc.data().p1team > 2){
                return(
            
                    <Redirect to="/mainP1" />
                ) 
            }
            else{
                function getRandomInt(min, max) {
                    min = Math.ceil(min);
                    max = Math.floor(max);
                    return Math.floor(Math.random() * (max - min) + min); //The maximum is exclusive and the minimum is inclusive
                  }
                let random = getRandomInt(0,2) 

                //alert(random)
                if(random === 0){
                    alert("asdasda 0")
                    return(
                        
                        <Redirect to="/mainP1" />
                    ) 
                }
                else{
                    alert("asdasda 1")
                    return(
            
                        <Redirect to="/mainP2" />
                    ) 
                }
            }
            
        })
        
    }
    
    useEffect(() => {
        getCurrentUserOnWeb();
    }, [])

    return(
        <p>Loading...</p>
    )
}

export default CheckRandom;
