import React, { useState } from 'react'
import { Redirect,Link } from 'react-router-dom'
import firebaseConfig from '../config'

const SignUp = () => {
    const [currentUser, setCurrentUser] = useState(null);

    const handleSubmit = (e) =>{
        e.preventDefault();
        const {email,password} = e.target.element;

        try {
            firebaseConfig.auth().createUserWithEmailAndPassword(email.value, password.value);
            setCurrentUser(true);
        }catch(error){
            alert(error);
        }
    }

    if(currentUser){
        return <Redirect to="/login"/>
    }

    return (
        <>
            <div className="container-fluid">
                <div className="row main-content bg-success text-center">
                    <div className="col-md-4 text-center company__info">
                        <span className="company__logo">
                            <h2><span className="fa fa-android"></span></h2>
                        </span>

                    </div>
                    <div className="col-md-8 col-xs-12 col-sm-12 login_form ">
                        <div className="container-fluid">
                            <div className="row">
                                <h2>Register</h2>
                            </div>
                            <div className="row">
                                <form id="register_form" onSubmit={handleSubmit}>
                                    <div className="row">
                                        <input type="text" name="email" id="email" className="form__input" placeholder="Email"
                                            required />

                                    </div>
                                    <div className="row">
                                        <input type="password" name="password" id="password" className="form__input"
                                            placeholder="Password" required />

                                    </div>
                                

                                    <div className="row">
                                        <button className="btn" type="submit">Register</button>
                                    </div>
                                </form>
                            </div>
                            <div className="row">
                                <p>Login? <Link to="/login" >Login Here</Link></p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default SignUp;
