import React, { useContext, useState, useEffect } from 'react'
import { Redirect } from 'react-router-dom'
import { AuthContext } from './auth'
import firebaseConfig from '../config'


const CheckRandom = () => {

    var generator = require('generate-password');

    var password = generator.generate({
        length: 10,
        numbers: true
    });
    const { currentUser } = useContext(AuthContext);
    const db = firebaseConfig.firestore();

    const onSubmit = () => {
        window.location.href = '/login'


    }

    return (

        <div className="container-fluid">
            <div className="row main-content text-center ">
                <div className="col-md-12 col-xs-12 col-sm-12 ">
                    <div className="container-fluid">
                        <div className="row"><h1>PASSCODE: {password}</h1></div>
                        <div className="row"><h2>Copy this passcode and back to login page</h2></div>



                        <button className="btnlogin btn-success" onClick={onSubmit}>Login</button>
                    </div>


                </div>


            </div>

        </div >



    )
}

export default CheckRandom;
