import React, { useContext, useState, useEffect } from 'react'
import { Redirect } from 'react-router-dom'
import { AuthContext } from './auth'
import firebaseConfig from '../config'
import Nav from './Nav'
import Twitch from './Twitch'
import BtnP1 from './BtnP1'
import { CustomDialog, useDialog, Alert } from 'react-st-modal'
import { TwitchEmbed, TwitchChat, TwitchClip, TwitchPlayer } from 'react-twitch-embed';

const MainP1 = () => {
    const { currentUser } = useContext(AuthContext);
    let countMessage = 0;
    const db = firebaseConfig.firestore();

    const [blogs, setBlogs] = useState([])
    const fetchBlogs = async () => {
        const data = await db.collection('leaderboard').orderBy("countSendMessage", "desc").get();
        setBlogs(data.docs.map(doc => doc.data()))

    }
    useEffect(() => {
        fetchBlogs();
    }, [])
    //console.log(blogs)


    const tmi = require('tmi.js');
    const client = new tmi.Client({
        options: { debug: true, messagesLogLevel: "info" },
        connection: {
            reconnect: true,
            secure: true
        },
        identity: {
            username: 'ligoligo12',
            password: 'oauth:2adeuj6p5ui8ujtxmqwkf55sk5janf'
        },
        channels: ['ligoligo12']
    });
    client.connect().catch(console.error);


    const handleP1 = () => {
        db.collection('leaderboard').doc(currentUser.uid).get().then(data => {
            countMessage = data.data().countSendMessage
            //console.log(data.data().countSendMessage)
            //alert(countMessage)
        }).then(() => {
            client.say(`#ligoligo12`, `@${currentUser.displayName}, p1+`);
            countMessage++
            db.collection('leaderboard').doc(currentUser.uid).update({

                countSendMessage: countMessage

            })
        })
    }
    const handleP2 = () => {
        db.collection('leaderboard').doc(currentUser.uid).get().then(data => {
            countMessage = data.data().countSendMessage
            //console.log(data.data().countSendMessage)
            //alert(countMessage)
        }).then(() => {
            client.say(`#ligoligo12`, `@${currentUser.displayName}, p2-`);
            countMessage++
            db.collection('leaderboard').doc(currentUser.uid).update({

                countSendMessage: countMessage

            })
        })
    }

    if (!currentUser) {
        return <Redirect to="/login" />;
    }
    if (currentUser) {
        
    }

    return (
        <div>
            <Nav/>
            <Twitch/>
            <BtnP1/>

            <div className="containerIF3">
                <div className="row row-cols-2">
                    <button className="btn btn-success" onClick={handleP1}>P1+</button>
                    <button className="btn btn-danger" onClick={handleP2}>P2-</button>
                </div>

            </div>

            <div class="container">
                <div class="row">
                    <div className="col-lg-8 mx-auto text-center mb-0 mt-3 ">
                        <h1>Leaderboard</h1>
                    </div>

                </div>

                <div class="row">
                    <div class="col-lg-12 mx-auto text-center mb-0 ">
                        <div class="responsive-iframe">
                            <iframe src="https://docs.google.com/spreadsheets/d/e/2PACX-1vSkQOavXwJjbSuUDdtspqjEwmbgCLc2pC7EromgysGZrmX0BJmm1x_6mRRqj71RJnMcj_n4CBld9McT/pubhtml?gid=349047516&amp;single=true&amp;widget=true&amp;headers=false" width="75%" height="300px" ></iframe>
                        </div>

                    </div>
                </div>
            </div>


        </div>





    )
    function CustomDialogContent() {
        //const dialog = useDialog();
        /*const nameList = temp.map(name =>{
            return(
                <li>{name.displayName}</li>
            )
        })*/
        //const [value, setValue] = useState();

        return (
            <div>
                <div class="container">
                    <div class="row">
                        <div class="col-lg-12 mx-auto text-center mb-0 ">
                            <div class="responsive-iframe">
                            <div className="row row-cols-2">
                            <p >Name</p>
                            <p >Score</p>
                            </div>
                                {
                                    blogs.map(blog => {
                                        return (
                                            <div className="row row-cols-2">                              
                                                <p key={blog.displayName}>{blog.displayName}</p>
                                                <p key={blog.displayName}>{blog.countSendMessage}</p>
                                            </div>
                                        )
                                    })
                                }
                            </div>
                        </div>
                    </div>
                </div>
            </div >
        );
    }
    function getData(data) {

        return (
            <div>
                {data.map((data) => (
                    <div>

                        <p>{data.displayName}</p>
                        <p>{data.countSendMessage}</p>
                    </div>
                ))}
            </div>
        )
        console.log(data);

    }

}

export default MainP1;
